   const eyeRect = eyeRef.current.getBoundingClientRect();      const eyeCenterX = eyeRect.left + eyeRect.width / 2;      const eyeCenterY = eyeRect.top + eyeRect.height / 2;      // 计算相对于眼睛中心的坐标      const dx = mousePos.x - eyeCenterX;      const dy = mousePos.y - eyeCenterY;      // 计算角度      const angle = Math.atan2(dy, dx) * 180 / Math.PI;      const normalizedAngle = (angle + 360) % 360;      // 确定方向索引（0-7）      const directionIndex = Math.round(normalizedAngle / 45) % 8;      // 预定义8个方向的位置偏移和旋转角度      const radius = 20; // 瞳孔偏移的半径，根据眼睛大小调整      const directions = [        { x: radius, y: 0, rotate: 0 },       // 0度        { x: radius * Math.cos(Math.PI/4), y: radius * Math.sin(Math.PI/4), rotate: 45 }, //45        { x: 0, y: radius, rotate: 90 },      //90        { x: -radius * Math.cos(Math.PI/4), y: radius * Math.sin(Math.PI/4), rotate: 135 }, //135        { x: -radius, y: 0, rotate: 180 },    //180        { x: -radius * Math.cos(Math.PI/4), y: -radius * Math.sin(Math.PI/4), rotate: 225 }, //225        { x: 0, y: -radius, rotate: 270 },    //270        { x: radius * Math.cos(Math.PI/4), y: -radius * Math.sin(Math.PI/4), rotate: 315 }, //315      ];      const { x, y, rotate } = directions[directionIndex];      setPupilStyle({        transform: \`translate({x}px, {y}px) rotate({rotate}deg)\`,      });    }  }, [mousePos]);  return (    <div className=\\"eye\\" ref={eyeRef}>      <div className=\\"pupil\\" style={pupilStyle}></div>    </div>  );};这样，每个眼睛的瞳孔会根据鼠标的位置移动到8个方向的位置，并且旋转对应的角度。接下来需要调整样式，确保眼睛和瞳孔的定位正确。例如，眼睛容器需要是相对定位，瞳孔是绝对定位，并且居中，然后通过transform移动位置。CSS可能如下：.character {  position: relative;  width: 100vw;  height: 100vh;  display: flex;  justify-content: center;  align-items: center;}.eyes {  display: flex;  gap: 50px;}.eye {  width: 100px;  height: 100px;  background-color: white;  border-radius: 50%;  position: relative;  overflow: hidden;}.pupil {  position: absolute;  width: 40px;  height: 40px;  background-color: black;  border-radius: 50%;  top: 50%; 